jsLib:
function registerFn(cookie) {
var {java,source} = this	
var su=source.getKey()

body={
   'username':`qaz${parseInt(Math.random()*1000000000000)}`,
    'password': '123456789',
    'repassword':'123456789',
     'email': `${parseInt(Math.random()*1000000000000)}@qq.com`,
    'checkcode':java.getVerificationCode(`${su}/checkcode.php?rand=${Math.random()}`),
     'act': "newuser",
     'accounttype': "useremail",
      'submit': ""
}

bodyString = Object.keys(body).reduce((pre,key)=>{
    pre += (pre!=''?'&':'') + `${key}=${body[key]}`
    return pre
},'')


option={
   'method': 'POST',
    'body': bodyString,
    'headers':{
        'Referer':su+'/register.php',
        "Origin":su,
    	}
}

  url=su+'/register.php?do=submit,'+JSON.stringify(option);

rs=java.ajax(url);
 if(/出现错误/.test(rs)){
   java.toast(`注册失败，重新注册！`)
   return false
 }


// token=cookie.getKey(url,"SESSION_ID");

java.log(cookie.getCookie(url))

  Cookie={
   "Cookie":cookie.getCookie(url),
   "User-Agent": "Mozilla/5.0 (Linux; Android 9; MIX 2 Build/PKQ1.190118.001; wv) AppleWebKit/537.36 (KHTML, like Gecko) Version/4.0 Chrome/80.0.3987.99 Mobile Safari/537.36"
  }
   header = JSON.stringify(Cookie);
   source.putLoginHeader(header);
   source.setVariable(`账号：${body.username},密码：${body.password}`)
   java.toast(`注册成功----${body.username}`)
}

function sleep(value){
var date = Date.now();
var currentDate = null;
do {
  currentDate = Date.now();
} while (currentDate - date < value);
}

